  labs(title="Boxplot of Two Groups", x="Group", y="Values") +
  theme_minimal()

ggplot(data_full, aes(x=group, y=values, fill=group)) +
  geom_boxplot() +
  scale_fill_manual(values=c("ModelFinder"="blue", "MixtureFinder"="red")) +
  labs(title="Boxplot of Two Groups", x="Group", y="Values") +
  theme_minimal()



# combine data_part and data_full
data_combined <- data.frame(
  values = c(data_part$values, data_full$values),
  group = factor(c(as.character(data_part$group), as.character(data_full$group))),
  dataset = factor(c(rep("Part", nrow(data_part)), rep("Full", nrow(data_full))))
)

data_combined$dataset <- factor(data_combined$dataset, levels = c("Part", "Full"),
                                labels = c("24species", "Full species"))


ggplot(data_combined, aes(x = interaction(dataset, group, lex.order = TRUE), y = values, fill = group)) +
  geom_boxplot() +
  scale_fill_manual(values = c("ModelFinder" = "grey", "MixtureFinder" = "orange")) +
  labs(y = "qCF Values") +
  theme_minimal() +
  theme(axis.text.x = element_text(angle = 45, hjust = 1),
        axis.text = element_text(size = 11, face = "bold"),  # x 和 y 轴的标签
        axis.title = element_text(size = 13, face = "bold")) +  # x轴标签旋转，方便阅读
  scale_x_discrete(limits = c("24species.ModelFinder", "24species.MixtureFinder", 
                              "Full species.ModelFinder", "Full species.MixtureFinder")) +
  xlab(NULL) 


